  `gift_card1` float(10,2) NOT NULL,
  `gift_card2` float(10,2) NOT NULL,
  `total_cost_paid` float(10,2) NOT NULL default '0.00' COMMENT 'total cost including item + shipping',
  `ship_cost` float(10,2) NOT NULL,
  `commision_transaction` text NOT NULL COMMENT 'transaction id for commision ',
  `commisionvalues_returned` text NOT NULL COMMENT 'all transaction values returned',
  `commision_status` tinyint(4) NOT NULL default '0' COMMENT '0-for unpaid 1 for paid',
  `commisionpaid_date` date NOT NULL COMMENT 'date on which commision paid',
  `package_name` varchar(255) NOT NULL COMMENT 'package name',
  `payment_status` tinyint(4) default '0' COMMENT '0-not paid 1for paid',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=516 DEFAULT CHARSET=utf8;

