CREATE TABLE `tbl_buyer_purchased_item` (
  `id` int(11) NOT NULL auto_increment,
  `item_id` int(11) NOT NULL,
  `quantity` int(11) NOT NULL default '0',
  `buyer_id` int(11) NOT NULL,
  `seller_id` int(11) NOT NULL,
  `amount` float(10,2) NOT NULL COMMENT 'cost of item paid',
  `commision_amount` varchar(255) NOT NULL COMMENT 'commision for items',
  `shipping_status` int(11) NOT NULL,
  `purchase_date` datetime NOT NULL,
  `paymentmode` varchar(255) character set latin1 collate latin1_general_ci NOT NULL,
